import Reac, { useState } from 'react';
import { Outlet } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome, faUser, faSignOutAlt,faLock, faGlobe, faSearch, faSignInAlt } from '@fortawesome/free-solid-svg-icons';
import { useNavigate }  from 'react-router-dom';
import useAuth from "../hooks/useAuth"

const Layout = () => {
    const [searchQuery, setSearchQuery] = useState('');
    const [filter, setFilter] = useState('top');
    const navigate = useNavigate();
    const { status } = useAuth()

    const [isSignInModalOpen, setIsSignInModalOpen] = useState(false);
    const [isSignOutModalOpen, setIsSignOutModalOpen] = useState(false);
    const openSignInModal = () => setIsSignInModalOpen(true);
    const closeSignInModal = () => setIsSignInModalOpen(false);

    const openSignOutModal = () => setIsSignOutModalOpen(true);
    const closeSignOutModal = () => setIsSignOutModalOpen(false);



    return (
        <div className="home-page">
            <div className="columns-container">
                <div className="left-column">
                    <div className="component__layout__navbar">
                        <div className="component__layout__logo" >
                            <img src="/img/logo.jpg" alt="Logo" />
                        </div>
                        <div className="component__layout__nav-buttons">
                            <button onClick={()=>navigate('/')}>
                                <FontAwesomeIcon icon={faHome} />
                                Home
                            </button>
                            <button>
                                <FontAwesomeIcon icon={faUser} />
                                Profile
                            </button>
                            <button onClick={()=>navigate("/signIn")}>
                                <FontAwesomeIcon icon={status==='guest'? faSignInAlt : faSignOutAlt} />
                                {status==='guest'? " Sign Up / Log In" : "Logout"}
                            </button>
                        </div>
                    </div>
                    <div className="component__layout__content">
                        {/* Outlet */}
                    </div>
                </div>
                <Outlet />
                <div className="right-column">
                    <div className="search-filters">
                        <div className="search-container">
                            <FontAwesomeIcon icon={faSearch} />
                            <input
                                type="text"
                                placeholder="Search events..."
                                value={searchQuery}
                                onChange={e => setSearchQuery(e.target.value)}
                            />
                        </div>
                        <div className="filter-container">
                            <button
                                className={`filter-button ${filter === 'top' ? 'active' : ''}`}
                                onClick={() => setFilter('top')}
                            >
                                Top X
                            </button>
                            <button
                                className={`filter-button ${filter === 'new' ? 'active' : ''}`}
                                onClick={() => setFilter('new')}
                            >
                                New X
                            </button>
                            <button
                                className={`filter-button ${filter === 'friends' ? 'active' : ''}`}
                                onClick={() => setFilter('friends')}
                            >
                                Friends
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Layout;
