import { useGetEventsQuery } from "./EventApiSlice";
import EventMini from "./eventMini";
import PulseLoader from 'react-spinners/PulseLoader'
import { useSelector } from 'react-redux';


const EventsList = (props) => {

    const {userId, isPublic, showAll} = props
    const filter = useSelector(state => state.filter);
    console.log(filter)
    const {
        data: events,
        isLoading,
        isSuccess,
        isError,
        error
    } = useGetEventsQuery('eventsList', {
        pollingInterval: 60*1000 ,
        refetchOnFocus: true,
        refetchOnMountOrArgChange: true,
    })
    let content

    if (isLoading) content = <PulseLoader color={'#FFF'} />
    if (isError) content = <p className="errmsg">{error?.data?.message}</p>

    if(isSuccess) {




        let { ids } = events
        if(ids){
            if(userId){
                ids = ids.filter(event => events.entities[event].owner === userId)
            }
            if(isPublic){
                ids = ids.filter(event => events.entities[event].public)
            }   

        }
        switch(filter){
            case 'all':
                break;
            case 'top':
                ids = ids.filter(event => events.entities[event].owner === userId)
                break;
            case 'new':
                ids = ids.filter(event => events.entities[event].public)
                break;
            case 'friends':
                ids = ids.filter(event => !events.entities[event].public)
                break;
            default:
                break;
        }
        const sortedIdsByLikes = ids.slice().sort((a, b) => events.entities[b].likes - events.entities[a].likes);
        let events_list = sortedIdsByLikes.map(eventId => <EventMini event={events?.entities[eventId]}/>)
        if(events_list)
        content = (
            <div className="event-list">

                {events_list}
            </div>
        )

    }
    return content;

}

export default EventsList
