import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSignInAlt } from "@fortawesome/free-solid-svg-icons";
import useTitle from "../../hooks/useTitle";
import { setCredentials } from './authSlice'
import { useLoginMutation } from './authApiSlice'
import { useAddNewUserMutation } from "../users/UserApiSlice";

const USER_REGEX = /^[A-z]{3,20}$/
const PWD_REGEX = /^[A-z0-9!@#$%]{4,12}$/

const SignUp = ({onClose, onOpen}) => {


    const [addNewUser, {
        isLoading,
        isSuccess,
        isError,
        error
    }] = useAddNewUserMutation()

    const navigate = useNavigate();

    const [username, setUsername] = useState('');
    const [validUsername, setValidUsername] = useState(false);
    const [password, setPassword] = useState('');
    const [validPassword, setValidPassword] = useState(false);



    useEffect(() => {
        setValidUsername(USER_REGEX.test(username))
    }, [username])

    useEffect(() => {
        setValidPassword(PWD_REGEX.test(password))
    }, [password])


    useEffect(() => {
        if (isSuccess) {
            setUsername('')
            setPassword('')
            navigate('/dash/users')
        }
    }, [isSuccess, navigate])



    const onUsernameChanged = e => setUsername(e.target.value);
    const onPasswordChanged = e => setPassword(e.target.value);

    const canSave = [validUsername, validPassword].every(Boolean) && !isLoading


    const onSaveUserClicked = async (e) => {
        e.preventDefault()
        if (canSave) {
            await addNewUser({ username, password })
        }
    }
    const errClass = isError ? "errmsg" : "offscreen"
    const validUserClass = !validUsername ? 'form__input--incomplete' : ''
    const validPwdClass = !validPassword ? 'form__input--incomplete' : ''


    const handleBackdropClick = (event) => {
        if (event.target.classList.contains('my-modal')) {
            onClose();
        }
    };
    const content = (
        <div className="my-modal" onClick={handleBackdropClick}>
            <div className="modal-content">
            <form className="form" onSubmit={onSaveUserClicked}>
                <div className="form__title-row">
                    <h2>Create an account</h2>
                    <div className="form__action-buttons">
                        <button
                            className="icon-button"
                            title="Sign In"
                        >
                            <FontAwesomeIcon icon={faSignInAlt} />
                        </button>
                    </div>
                </div>
                <div>Error:</div>
                <label className="form__label" htmlFor="username">
                    Username:</label>
                <input
                    className="form__input"
                    id="username"
                    name="username"
                    type="text"
                    autoComplete="off"
                    value={username}
                    onChange={onUsernameChanged}
                />

                <label className="form__label" htmlFor="password">
                    Password:</label>
                <input
                    className="form__input"
                    id="password"
                    name="password"
                    type="password"
                    value={password}
                    onChange={onPasswordChanged}
                />
            </form>
            <div className="signup-section">
                <p className="signup-text">Already have an account?</p>
                <Link onClick={()=>{onOpen(); onClose()}} className="signup-link">
                    Sign In
                </Link>
            </div>
            </div>
        </div>
    );

    return content;
};

export default SignUp;
