import { useGetEventsQuery } from "./EventApiSlice";
import EventMini from "./eventMini";
import PulseLoader from 'react-spinners/PulseLoader'


const EventsList = (props) => {

    const {userId, isPublic} = props

    const {
        data: events,
        isLoading,
        isSuccess,
        isError,
        error
    } = useGetEventsQuery('eventsList', {
        pollingInterval: 60*1000 ,
        refetchOnFocus: true,
        refetchOnMountOrArgChange: true,
    })
    let content

    if (isLoading) content = <PulseLoader color={'#FFF'} />
    if (isError) content = <p className="errmsg">{error?.data?.message}</p>

    if(isSuccess) {

        const sortedIdsByLikes = events.ids.slice().sort((a, b) => events.entities[b].likes - events.entities[a].likes);


        const { ids } = events

        const events_list = sortedIdsByLikes.map(eventId => <EventMini event={events?.entities[eventId]}/>)
        if(events_list){
            if(userId){
                events_list = events_list.filter(event => event.owner === userId)
            }
            if(isPublic){
                events_listevents_list.filter(event => event.public)
            }   
        }
        content = (
            <div className="event-list">
                {events_list}
            </div>
        )

    }
    return content;

}

export default EventsList
