import React, { useState, useEffect } from 'react';

const CountdownBar = ({ time, date, timezone }) => {

const newDate = date.split('-').reverse().join('-');
const targetDateTime = new Date(`${newDate}T${time}${timezone}`);
const currentTime = new Date();

console.log(c, targetDateTime,  currentTime, date, time, timezone)
const [progress, setProgress] = useState(100);

useEffect(() => {
  const interval = setInterval(() => {
    const remaining = Math.max(targetDateTime - new Date(), 0);
    const timeRemaining = Math.max(remaining, 0);
    const newProgress = ((targetDateTime - currentTime - timeRemaining) / (targetDateTime - currentTime)) * 100;
    setProgress(newProgress);

    if (remaining === 0) {
      clearInterval(interval);
    }
  }, 1000);

  return () => clearInterval(interval);
}, [targetDateTime, currentTime]);

return (
  <div className="countdown-bar">
    <div className="bar" style={{ width: `${progress}%` }}></div>
  </div>
);
};

export default CountdownBar;
