import { useState, useEffect } from "react"
import { useNavigate } from "react-router-dom"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSave } from "@fortawesome/free-solid-svg-icons"
import useTitle from "../../hooks/useTitle"
import { loginUser } from "./authApiSlice"  // Assuming you have an authentication API slice
import { useAddNewUserMutation } from "../users/UserApiSlice"

const USER_REGEX = /^[A-z]{3,20}$/
const PWD_REGEX = /^[A-z0-9!@#$%]{4,12}$/

const SignUp = () => {

    const [addNewUser, {
        isLoading,
        isSuccess,
        isError,
        error
    }] = useAddNewUserMutation()

    const onSaveUserClicked = async (e) => {
        e.preventDefault()
        await addNewUser({ username, password })

    }

    const navigate = useNavigate()

    const [username, setUsername] = useState('')
    const [validUsername, setValidUsername] = useState(false)
    const [password, setPassword] = useState('')
    const [validPassword, setValidPassword] = useState(false)


    useEffect(() => {
        setValidUsername(USER_REGEX.test(username))
    }, [username])

    useEffect(() => {
        setValidPassword(PWD_REGEX.test(password))
    }, [password])

    const canSignIn = [validUsername, validPassword].every(Boolean)

    const onUsernameChanged = e => setUsername(e.target.value)
    const onPasswordChanged = e => setPassword(e.target.value)

    const validUserClass = !validUsername ? 'form__input--incomplete' : ''
    const validPwdClass = !validPassword ? 'form__input--incomplete' : ''

    const content = (
        <form className="form" onSubmit={onSaveUserClicked}>
            <div className="form__title-row">
                <h2>Sign In</h2>
                <div className="form__action-buttons">
                    <button
                        className="icon-button"
                        title="Sign In"
                        disabled={!canSignIn}
                    >
                        <FontAwesomeIcon icon={faSave} />
                    </button>
                </div>
            </div>
            <label className="form__label" htmlFor="username">
                Username: <span className="nowrap">[3-20 letters]</span></label>
            <input
                className={`form__input ${validUserClass}`}
                id="username"
                name="username"
                type="text"
                autoComplete="off"
                value={username}
                onChange={onUsernameChanged}
            />

            <label className="form__label" htmlFor="password">
                Password: <span className="nowrap">[4-12 chars incl. !@#$%]</span></label>
            <input
                className={`form__input ${validPwdClass}`}
                id="password"
                name="password"
                type="password"
                value={password}
                onChange={onPasswordChanged}
            />
        </form>
    )

    return content
}

export default SignUp
