import React, { useState, useRef } from 'react';
import { useAddNewEventMutation } from './EventApiSlice';
import EventMini from './ev';
import useAuth from '../../hooks/useAuth';
const EventCreationForm = ({ userId }) => {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [image, setImage] = useState('');
  const [date, setDate] = useState('');
  // ... other state variables for form fields

  const [createEvent, { isLoading, isSuccess, isError, error }] = useAddNewEventMutation(); // Initialize the mutation

  const contentTextAreaRef = useRef(null);

  const handleTextAreaChange = (event) => {
    const textarea = event.target;
    textarea.style.height = 'auto';
    textarea.style.height = `${textarea.scrollHeight}px`;
    setContent(event.target.value);
  };

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setImage(e.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleCreateNewEvent = async (e) => {
    e.preventDefault();

    // Call the mutation to create a new event
    const eventData = {
      title,
      content,
      image,
      date,
      owner: userId,
      // ... other fields
    };

    try {
      await createEvent(eventData);
      // Handle success
      console.log('Event created successfully!');
    } catch (error) {
      // Handle error
      console.error('Error creating event:', error);
    }
  };

  return (
    <div className="event-creation-form">
      <h2>Create New Event</h2>
      <form onSubmit={handleCreateNewEvent}>
        <div className="form-group">
          <label>Title</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>Content</label>
          <textarea
            ref={contentTextAreaRef}
            value={content}
            onChange={handleTextAreaChange}
          ></textarea>
        </div>
        <div className="form-group">
          <label>Upload Image</label>
          <input
            type="file"
            accept="image/*"
            onChange={handleImageUpload}
          />
        </div>
        <div className="form-group">
          <label>Date</label>
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
          />
        </div>
        {/* ... other form fields */}
        <button type="submit" disabled={isLoading}>
          Create Event
        </button>
        {isSuccess && <p>Event created successfully!</p>}
        {isError && <p>Error creating event. Please try again later.</p>}
      </form>
      <div className="event-preview">
        <h3>Event Preview</h3>
        {title && (
          <EventMini
            event = {
                {
                    title: title,
                    content: content,
                    image: image,
                    owner: userId,
                    date: date,


                }
            }
          />
        )}
      </div>
    </div>
  );
};

export default EventCreationForm;
