import React, { useState } from 'react';
import useAuth from '../../hooks/useAuth';
import { useGetUsersQuery } from './UserApiSlice';
import { useUpdateUserMutation } from './UserApiSlice';
import { useParams } from 'react-router-dom';

const People = () => {
    const [updateUser, {
        isLoading,
        isSuccess,
        isError,
        error
    }] = useUpdateUserMutation()

    const {
        data: users,
        isLoading: usersLoading,
        isSuccess: usersSuccess,
        isError: usersError,
        error: usersErrorData
    } = useGetUsersQuery('usersList', {
        pollingInterval: 60*1000,
        refetchOnFocus: true,
        refetchOnMountOrArgChange: true
    })

    const [searchQuery, setSearchQuery] = useState('');
    const [selectedUser, setSelectedUser] = useState(null);
    const [hasSentRequest, setHasSentRequest] = useState(false);
    // Function to accept a friend request
    const acceptRequest = (user, requestId) => {

        const requestUser = users.entities[requestId]

        if(!user || !requestId || !requestUser) return;
        const updatedUser = {
            ...user,
            requests: user.requests.filter(request => request !== requestId),
            friends: [...user.friends, requestId]
        }
        updateUser(updatedUser)
        const updatedRequestUser = {
            ...requestUser,
            friends: [...requestUser.friends, user.id]
        }
        updateUser(updatedRequestUser)

    };

    // Function to reject a friend request
    const rejectRequest = (user, requestId) => {
        if(!user || !requestId) return;
        const updatedUser = {
            ...user,
            requests: user.requests.filter(request => request !== requestId)
        }
        updateUser(updatedUser)
    };

    // Function to search for users


    // Function to view a user's profile
    const viewUserProfile = userId => {
      const selected = users.find(user => user.id === userId);
      setSelectedUser(selected);
    };
    const { ids } = usersSuccess ? users : { ids: [] };


    const searchUsers = query => {
        setSearchQuery(query);
        setSelectedUser(null); // Clear selected user
    };

    const allUsers = usersSuccess ? ids.map(id => users.entities[id]) : [];
    const filteredUsers = usersSuccess
        ? allUsers.filter(user => user.username.toLowerCase().includes(searchQuery.toLowerCase())).sort(((a, b) => a.username.localeCompare(b.username)))
        : [];


    const top5Users = filteredUsers.slice(0, 5); // Get the top 5 users


    const { id } = useParams();
    let currentUser = users?.entities[id]
    //sort be username
    const requestedUsers = currentUser?.requests.filter(item => item !== '').map(item => users?.entities[item])
    const friends = currentUser?.friends.filter(item => item !== '').map(item => users?.entities[item])
    return (
      <div className="friends-container">
        <h2>Friend Requests</h2>
        {requestedUsers?.map(user => (
          <div className="friends-container-main" key={user.id}>


            <img src={user.img} alt={user.username} />
            <h3>{user.username}</h3>
            <div className="friends-container-buttons">
                <button onClick={() => acceptRequest(user, user.requests[0])}>Accept</button>
                <button onClick={() => rejectRequest(user, user.requests[0])}>Reject</button>
            </div>
          </div>
        ))}

        <h2>Your Friends</h2>
        {friends?.map(user => (
          <div className="friends-container-main" key={user.id}>


            <img src={user.img} alt={user.username} />
            <h3>{user.username}</h3>
            <div className="friends-container-buttons">
                <button onClick={() => acceptRequest(user, user.requests[0])}>Remove Friend</button>
            </div>

          </div>
        ))}

        <h1>Search People</h1>
            <input
                type="text"
                placeholder="Search users..."
                value={searchQuery}
                onChange={e => searchUsers(e.target.value)}
            />
            <div className="friends-container" >
                <h1>Top 5 Results</h1>
                {top5Users.map(user => (
                    <div className="friends-container-main" key={user.id}>

                    <img src={user.img} alt={user.username} />
                    <h3>{user.username}</h3>
                    <div className="friends-container-buttons">
                        <button onClick={() => acceptRequest(user, user.requests[0])}>Send Friend Request</button>
                    </div>
                  </div>

                ))}

            </div>

        </div>
    );
  };

  export default People;
