import React, { useState, useRef } from 'react';
import { useAddNewEventMutation } from './EventApiSlice';

const EventCreationForm = ({ userId }) => {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [image, setImage] = useState(''); // Add image state
  const [date, setDate] = useState(''); // Add date state

  const [createEvent, { isLoading, isSuccess, isError, error }] = useAddNewEventMutation(); // Initialize the mutation
  const contentTextAreaRef = useRef(null);
  const handleTextAreaChange = (event) => {
    const textarea = event.target;
    textarea.style.height = 'auto';
    textarea.style.height = `${textarea.scrollHeight}px`;
    setContent(event.target.value);
  };
  const handleCreateNewEvent = async (e) => {
    e.preventDefault();

    // Call the mutation to create a new event
    const eventData = {
      title,
      content,
      owner: userId,
      // ... other fields
    };

    try {
      await createEvent(eventData);
      // Handle success
      console.log('Event created successfully!');
    } catch (error) {
      // Handle error
      console.error('Error creating event:', error);
    }
  };

  return (
    <div className="event-creation-form">
      <h2>Create New Event</h2>
      <form onSubmit={handleCreateNewEvent}>
        <div className="form-group">
          <label>Title</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>Content</label>
          <textarea
            ref={contentTextAreaRef}
            value={content}
            onChange={handleTextAreaChange}
          ></textarea>
        </div>
        <div className="form-group">
          <label>Image URL</label>
          <input
            type="text"
            value={image}
            onChange={(e) => setImage(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>Date</label>
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
          />
        </div>
        {/* ... other form fields */}
        <button type="submit" disabled={isLoading}>
          Create Event
        </button>
        {isSuccess && <p>Event created successfully!</p>}
        {isError && <p>Error creating event. Please try again later.</p>}
      </form>
    </div>
  );
};
export default EventCreationForm;
