import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faLock, faGlobe, faSearch } from '@fortawesome/free-solid-svg-icons';

const dummyEvents = [
    {
        title: "Event 1",
        image: "https://imgur.com/YHX1D29",
        about: "Description of Event 1",
        invited: ["https://imgur.com/ouXvRND", "https://imgur.com/ouXvRND"]
    },
    {
        title: "Event 2",
        image: "https://imgur.com/YHX1D29",
        about: "Description of Event 2",
        invited: ["https://imgur.com/ouXvRND", "https://imgur.com/ouXvRND"]
    },
    // Add more dummy events
];

const Public = () => {
    const [isPublic, setIsPublic] = useState(true);
    const [searchQuery, setSearchQuery] = useState('');
    const [filter, setFilter] = useState('top');

    return (
        <div className="home-page">
            <div className="toggle-container">
                <button
                    className={`toggle-button ${isPublic ? 'active' : ''}`}
                    onClick={() => setIsPublic(true)}
                >
                    <FontAwesomeIcon icon={faGlobe} /> Public
                </button>
                <button
                    className={`toggle-button ${!isPublic ? 'active' : ''}`}
                    onClick={() => setIsPublic(false)}
                >
                    <FontAwesomeIcon icon={faLock} /> Private
                </button>
                <div className="search-container">
                    <FontAwesomeIcon icon={faSearch} />
                    <input
                        type="text"
                        placeholder="Search events..."
                        value={searchQuery}
                        onChange={e => setSearchQuery(e.target.value)}
                    />
                </div>
            </div>
            <div className="filter-container">
                <button
                    className={`filter-button ${filter === 'top' ? 'active' : ''}`}
                    onClick={() => setFilter('top')}
                >
                    Top X
                </button>
                <button
                    className={`filter-button ${filter === 'new' ? 'active' : ''}`}
                    onClick={() => setFilter('new')}
                >
                    New X
                </button>
                <button
                    className={`filter-button ${filter === 'friends' ? 'active' : ''}`}
                    onClick={() => setFilter('friends')}
                >
                    Friends
                </button>
            </div>
            <div className="event-list">
                {dummyEvents.map((event, index) => (
                    <div className="event" key={index}>
                        <img src={event.image} alt={`Event ${index}`} />
                        <h3>{event.title}</h3>
                        <p>{event.about}</p>
                        <div className="invited-list">
                            {event.invited.map((invitedImg, invitedIndex) => (
                                <img
                                    key={invitedIndex}
                                    src={invitedImg}
                                    alt={`Invited ${invitedIndex}`}
                                />
                            ))}
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Public;
