import React from 'react';
import { useGetUsersQuery } from './UserApiSlice';
import { useParams } from 'react-router-dom'
import EventsList from '../events/EventsList';
import { useAuth } from '.'

const UserHome = () => {
    const { id: loggedInUserId } = useAuth();

    const { id } = useParams();
    console.log("id: ", id);
    const { user } = useGetUsersQuery("usersList", {
        selectFromResult: ({ data }) => ({
            user: data?.entities[id]
        }),
    });

    // Check if id matches loggedInUserId
    const isCurrentUser = id === loggedInUserId;

    return (
        <div className="user-container">
          <div className="user-info-container">
            <div className="user-profile">
                <img src={user?.img} alt="User Profile" />
                <h2>{user?.username}</h2>
            </div>
            <div className="user-about">
                <h3>About Me</h3>
                {isCurrentUser ? (
                    <p>Edit your about me content here...</p>
                ) : (
                    <p>{user?.about.length > 100 ? user?.about.substring(0, 100) + '...' : user?.about}</p>
                )}
            </div>
          </div>
          <div className="user-events">
            <h3>Public Events</h3>
            <EventsList userId={id} isPublic={true}/>
          </div>
        </div>
      );
};

export default UserHome;
